<script>
    document.querySelectorAll(".scroll-card").forEach(card => {
        const scrollImage = card.querySelector(".scroll-image");
        const img = scrollImage.querySelector("img");

        // Wait for image to load
        img.onload = () => {
            const containerHeight = card.offsetHeight;
            const imageHeight = img.naturalHeight;
            const scrollAmount = imageHeight - containerHeight;

            card.addEventListener("mouseenter", () => {
                scrollImage.style.transition = "transform 3s linear";
                scrollImage.style.transform = `translateY(-${scrollAmount}px)`;
            });

            card.addEventListener("mouseleave", () => {
                scrollImage.style.transition = "transform 2s ease";
                scrollImage.style.transform = "translateY(0)";
            });
        };
    });
</script>

